INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2352, 3, 'Sie <em>fiel</em> im Wettkampf immer weiter <em>zurück</em>.', 'She kept <em>falling behind</em> further in the competition.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2352, 4, 'Sein Gesundheitszustand war leider wieder <em>zurückgefallen</em>.', 'Unfortunately, his health condition had <em>regressed</em> again.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2353, 1, 'Sie <em>hat</em> ihre Tasche im Zug <em>zurückgelassen</em>.', 'She <em>left</em> her bag <em>behind</em> on the train.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2353, 2, 'Er <em>hat</em> seine Freunde <em>zurückgelassen</em>.', 'He <em>abandoned</em> his friends.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2353, 3, 'Die Konkurrenz hat sie weit <em>zurückgelassen</em>.', 'The competition has <em>left</em> them far <em>behind</em>.');
