INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2355, 3, 'Der Soldat <em>kehrte</em> nach dem Krieg <em>zurück</em> nach Hause.', 'The soldier <em>went back</em> home after the war.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2355, 4, 'Sie <em>kehrte</em> nach langer Zeit finally <em>zurück</em> nach Hause.', 'She finally <em>returned home</em> after a long time.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2356, 1, 'Die Soldaten <em>kehrten</em> nach dem Einsatz <em>zurück</em>.', 'The soldiers <em>returned</em> after the deployment.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2356, 2, 'Sie <em>kehrte</em> nach einem Jahr <em>zurück</em>.', 'She <em>came back</em> after a year.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2356, 3, 'Wir <em>kehrten</em> schnell ins Hotel <em>zurück</em>.', 'We quickly <em>went back</em> to the hotel.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2356, 4, 'Die Änderungen <em>kehrten</em> nach dem Update <em>zurück</em>.', 'The changes <em>reverted</em> after the update.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2357, 1, 'In den <em>zurückliegenden</em> Jahren hat sich viel verändert.', 'In the <em>previous</em> years, a lot has changed.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2357, 2, 'Wir schauten auf die <em>zurückliegenden</em> Monate.', 'We looked back on the <em>past</em> months.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2357, 3, 'Die <em>zurückliegenden</em> Ereignisse waren entscheidend.', 'The <em>prior</em> events were crucial.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2358, 1, 'Das Schiff begann nach dem Sturm langsam <em>zurückzusinken</em>.', 'The ship began to <em>sink back</em> slowly after the storm.');
