INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2397, 4, 'Eine kleine <em>Änderung</em> an der Maschine kann große Auswirkungen haben.', 'A small <em>adjustment</em> to the machine can have big effects.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2398, 1, 'Sie hat große <em>Ängste</em> vor der Zukunft.', 'She has great <em>fears</em> about the future.');
