INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2399, 3, 'Die Gesundheitsprobleme <em>ängstigten</em> ihn.', 'The health problems <em>worried</em> him.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2400, 1, 'Sie war <em>ängstlich</em>, als sie die Nachricht hörte.', 'She was <em>anxious</em> when she heard the news.');
