INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2407, 2, 'Die <em>Öffnung</em> in der Wand war groß genug, um hindurchzusehen.', 'The <em>hole</em> in the wall was big enough to see through.');
