INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2412, 1, 'Nach all den Wiederholungen war sie <em>überdrüssig</em> <em>an</em> den ständigen Erklärungen.', 'After all the repetitions, she was <em>weary</em> <em>of</em> the constant explanations.');
