INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2412, 3, 'Die Wissenschaftlerin wurde <em>überdrüssig</em> <em>von</em> den immer gleichen Experimenten.', 'The scientist grew <em>tired</em> <em>of</em> the same old experiments.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2412, 4, 'Sie war <em>überdrüssig</em> <em>an</em> den endlosen Diskussionen.', 'She was <em>sick</em> <em>of</em> the endless discussions.');
