INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2418, 1, 'Der <em>Überhang</em> der Baumäste lässt viel Schatten.', 'The <em>overhang</em> of the tree branches casts plenty of shade.');
