INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2419, 3, 'Nach langem Zögern <em>überließ</em> sie ihn ihrer besten Freundin.', 'After a long hesitation, she <em>gave</em> him <em>up</em> to her best friend.');
