INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2420, 4, 'Wir <em>überließen</em> das alte Haus seinem Schicksal.', 'We <em>abandoned</em> the old house to its fate.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2421, 1, 'Ein wichtiger <em>Überlebenssatz</em> in der Wildnis ist, immer Wasser zu finden.', 'An important <em>survival rule</em> in the wilderness is to always find water.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2421, 2, 'Der <em>Überlebenssatz</em> besagt, dass man niemals die Hoffnung aufgeben sollte.', 'The <em>survival principle</em> states that you should never give up hope.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2421, 3, 'Ihr <em>Überlebenssatz</em> während der Krise war, ruhig und besonnen zu bleiben.', 'Her <em>survival maxim</em> during the crisis was to remain calm and collected.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2422, 1, 'Ein wichtiger <em>Überlebenssatz</em> lautet: "Bleib ruhig in einer Notsituation."', 'An important <em>survival rule</em> says: "Stay calm in an emergency situation."');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2422, 2, 'Der <em>Überlebenssatz</em> ist, dass man sich immer auf seine Intuition verlassen sollte.', 'The <em>survival saying</em> is that one should always trust their intuition.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2423, 1, 'Wir sollten das sorgfältig <em>überlegen</em>, bevor wir eine Entscheidung treffen.', 'We should <em>consider</em> it carefully before making a decision.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2423, 2, 'Sie <em>überlegte</em> lange, bevor sie antwortete.', 'She <em>thought about</em> it for a long time before she answered.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2423, 3, 'Nach einigen Minuten des <em>Überlegens</em> kam er zu einer Entscheidung.', 'After a few minutes of <em>deliberation</em>, he came to a decision.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2423, 4, 'Er nahm sich Zeit, um alles zu <em>überlegen</em>.', 'He took some time to <em>reflect</em> on everything.');
