INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2424, 1, 'Die <em>Überlegenheit</em> der Mannschaft war deutlich zu sehen.', 'The <em>superiority</em> of the team was clearly evident.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2424, 2, 'Technologische <em>Überlegenheit</em> ist entscheidend für den Erfolg.', 'Technological <em>superiority</em> is crucial for success.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2424, 3, 'Ihre <em>Überlegenheit</em> im Spiel war überwältigend.', 'Their <em>dominance</em> in the game was overwhelming.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2424, 4, 'Die <em>Überlegenheit</em> seiner Argumente überzeugte alle.', 'The <em>advantage</em> of his arguments convinced everyone.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2425, 1, 'Die <em>übermächtigen</em> Gegner machten einen Sieg unmöglich.', 'The <em>overpowering</em> opponents made victory impossible.');
