INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2425, 3, 'Er fühlte sich den <em>übermächtigen</em> Herausforderungen des Lebens nicht gewachsen.', 'He felt ill-equipped to handle the <em>vastly superior</em> challenges of life.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2425, 4, 'Ihr <em>übermächtigen</em> Ehrgeiz schadete ihrer Beziehung.', 'Her <em>domineering</em> ambition damaged her relationship.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2426, 1, 'Sie <em>überprüft</em> jeden Tag ihre E-Mails.', 'She <em>checks</em> her emails every day.');
