INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2427, 1, 'Die <em>Überprüfung</em> der Daten dauerte lange.', 'The <em>check</em> of the data took a long time.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2427, 2, 'Eine <em>Überprüfung</em> ist notwendig, um Fehler zu finden.', 'A <em>verification</em> is necessary to find errors.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2427, 3, 'Die <em>Überprüfung</em> des Sicherheitsprotokolls ergab keine Mängel.', 'The <em>examination</em> of the security protocol revealed no deficiencies.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2428, 1, 'Sie <em>überquert</em> die Straße.', 'She <em>crosses</em> the street.');
