INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2430, 3, 'Die <em>Überraschung</em> des Abends war der unerwartete Gast.', 'The <em>revelation</em> of the evening was the unexpected guest.');
INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2431, 1, 'Ich musste ihn lange <em>überreden</em>, bevor er zustimmte.', 'I had to <em>persuade</em> him for a long time before he agreed.');
