INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2433, 1, 'Der <em>Überstand</em> des Dachs schützt die Fenster vor Regen.', 'The <em>overhang</em> of the roof protects the windows from rain.');
