INSERT INTO word_contexts (word_id, context_id, original_text, translated_text) VALUES (2433, 3, 'Nach dem Sturm zeigte der Baum einen beeindruckenden <em>Überstand</em>.', 'After the storm, the tree showed an impressive <em>resilience</em>.');
